import React, { useState } from 'react';
import LoadingAnimation from './LoadingAnimation';
import ResultDisplay from './ResultDisplay';
import axios from 'axios';
import { db } from './firebase.js';

export default function HomePage() {
    const [formData, setFormData] = useState({
        firstName: '',
        email: '',
        phoneNumber: '',
        transcript: ''
    });

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        // Add a new document with a generated ID
        db.collection("submissions").add({
            ...formData,
            timestamp: firebase.firestore.FieldValue.serverTimestamp() // Adds a server timestamp for when the form was submitted
        })
        .then(() => {
            console.log("Document successfully written!");
        })
        .catch((error) => {
            console.error("Error writing document: ", error);
        });
    };

    return (
        <div>
            <h1>Submit your sales call transcript</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="firstName" placeholder="First Name" onChange={handleInputChange} />
                <input type="email" name="email" placeholder="Email" onChange={handleInputChange} />
                <input type="tel" name="phoneNumber" placeholder="Phone Number" onChange={handleInputChange} />
                <textarea name="transcript" placeholder="Paste your transcript here" onChange={handleInputChange}></textarea>
                <button type="submit">Submit</button>
            </form>
            {loading && <LoadingAnimation />}
            {result && <ResultDisplay result={result} />}
        </div>
    );
}
