// HomePage.jsx
import React, { useState } from 'react';
import axios from 'axios';
import Header from './Header';
import Footer from './Footer';
import ThankYouPage from './ThankYouPage';

const HomePage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    email: '',
    phoneNumber: '',
    transcript: ''
  });
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/submit', formData);
      console.log(response.data.message); // Display or log the success message
      setSubmitted(true); // Set the submission flag
    } catch (error) {
      console.error("Error submitting form: ", error);
      alert("Failed to submit form!"); // Alert or handle the error visually
    }
  };

  if (submitted) {
    return <ThankYouPage email={formData.email} phoneNumber={formData.phoneNumber} />;
  }

  return (
    <div>
      <Header />
      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-semibold mb-4">Submit your sales call transcript</h1>
        <form onSubmit={handleSubmit} className="max-w-md mx-auto">
          <input type="text" name="firstName" placeholder="First Name" onChange={handleInputChange} className="input-field" />
          <input type="email" name="email" placeholder="Email" onChange={handleInputChange} className="input-field" />
          <input type="tel" name="phoneNumber" placeholder="Phone Number" onChange={handleInputChange} className="input-field" />
          <textarea name="transcript" placeholder="Paste your transcript here" onChange={handleInputChange} className="input-field"></textarea>
          <button type="submit" className="btn">Submit</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default HomePage;
