import React, { useState } from 'react';
import axios from 'axios';
import Header from './Header';
import Footer from './Footer';
import ThankYouPage from './ThankYouPage';

const HomePage = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    email: '',
    phoneNumber: '',
    transcript: ''
  });
  const [submitted, setSubmitted] = useState(false);

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5001/submit', formData);
      console.log(response.data.message); // Display or log the success message
      setSubmitted(true); // Set the submission flag
    } catch (error) {
      console.error("Error submitting form: ", error);
      alert("Failed to submit form!"); // Alert or handle the error visually
    }
  };

  if (submitted) {
    return <ThankYouPage email={formData.email} phoneNumber={formData.phoneNumber} />;
  }

  return (
    <div>
      <Header />
      <div className="bg-gray-200 min-h-screen flex flex-col justify-center items-center">
        <div className="bg-white shadow-md rounded-lg p-8 max-w-md w-full">
          <h1 className="text-3xl font-semibold mb-6 text-center">Submit your sales call transcript</h1>
          <form onSubmit={handleSubmit} className="space-y-4">
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              onChange={handleInputChange}
              className="input-field"
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              onChange={handleInputChange}
              className="input-field"
              required
            />
            <input
              type="tel"
              name="phoneNumber"
              placeholder="Phone Number"
              onChange={handleInputChange}
              className="input-field"
              required
            />
            <textarea
              name="transcript"
              placeholder="Paste your transcript here"
              onChange={handleInputChange}
              className="input-field"
              rows="4"
              required
            ></textarea>
            <button
              type="submit"
              className="btn bg-blue-500 hover:bg-blue-600 text-white font-semibold py-2 px-4 rounded"
            >
              Submit
            </button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default HomePage;
