import React, { useState } from 'react';
import LoadingAnimation from './LoadingAnimation';
import ResultDisplay from './ResultDisplay';
import { db, firebase } from '../firebase';  // Corrected import

export default function HomePage() {
    const [formData, setFormData] = useState({
        firstName: '',
        email: '',
        phoneNumber: '',
        transcript: ''
    });
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true); // Set loading to true
        db.collection("submissions").add({
            ...formData,
            timestamp: firebase.firestore.FieldValue.serverTimestamp()
        })
        .then((docRef) => {
            console.log("Document successfully written with ID: ", docRef.id);
            setLoading(false); // Set loading to false after success
            setResult("Submission successful!"); // Setting a result to display
        })
        .catch((error) => {
            console.error("Error writing document: ", error);
            setLoading(false); // Ensure to set loading to false on error as well
        });
    };

    return (
        <div>
            <h1>Submit your sales call transcript</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="firstName" placeholder="First Name" onChange={handleInputChange} />
                <input type="email" name="email" placeholder="Email" onChange={handleInputChange} />
                <input type="tel" name="phoneNumber" placeholder="Phone Number" onChange={handleInputChange} />
                <textarea name="transcript" placeholder="Paste your transcript here" onChange={handleInputChange}></textarea>
                <button type="submit">Submit</button>
            </form>
            {loading && <LoadingAnimation />}
            {result && <ResultDisplay result={result} />}
        </div>
    );
}
