import React, { useState } from 'react';
import LoadingAnimation from './LoadingAnimation';
import ResultDisplay from './ResultDisplay'; 
import axios from 'axios';

export default function HomePage() {
    const [formData, setFormData] = useState({
        firstName: '',
        email: '',
        phoneNumber: '',
        transcript: ''
    });
    const [loading, setLoading] = useState(false);
    const [result, setResult] = useState(null);

    const handleInputChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        try {
            const response = await axios.post('http://localhost:5000/submit', formData);
            console.log(response.data.message); // Display or log the success message
            setResult(response.data.message); // Optionally display a success message in the UI
            setLoading(false);
        } catch (error) {
            console.error("Error submitting form: ", error);
            setResult("Failed to submit form!"); // Optionally display an error message in the UI
            setLoading(false);
        }
    };

    return (
        <div>
            <h1>Submit your sales call transcript</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" name="firstName" placeholder="First Name" onChange={handleInputChange} />
                <input type="email" name="email" placeholder="Email" onChange={handleInputChange} />
                <input type="tel" name="phoneNumber" placeholder="Phone Number" onChange={handleInputChange} />
                <textarea name="transcript" placeholder="Paste your transcript here" onChange={handleInputChange}></textarea>
                <button type="submit">Submit</button>
            </form>
            {loading && <LoadingAnimation />}
            {result && <ResultDisplay result={result} />}
        </div>
    );

}
