import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, useNavigate } from 'react-router-dom';  // Ensure proper imports
import Header from './Header';
import Footer from './Footer';
import ThankYouPage from './ThankYouPage';
import LoginPage from './LoginPage';
import { auth, db } from '../firebase';
import { onAuthStateChanged, signOut } from 'firebase/auth';
import { doc, getDoc } from 'firebase/firestore';
import { BrowserRouter as Router } from 'react-router-dom';


const HomePage = () => {
  const [user, setUser] = useState(null);
  const [formData, setFormData] = useState({
    firstName: '',
    email: '',
    phoneNumber: '',
    transcript: ''
  });
  const [submissions, setSubmissions] = useState([]);
  const [isPopupOpen, setIsPopupOpen] = useState(false);

  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) {
        setUser(user);
        fetchSubmissions(user.uid);
      } else {
        setUser(null);
      }
    });
  }, []);

  const fetchSubmissions = async (userId) => {
    const userDoc = doc(db, 'users', userId);
    const userSnapshot = await getDoc(userDoc);
    if (userSnapshot.exists()) {
      const userData = userSnapshot.data();
      setSubmissions(userData.submissions || []);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e, navigate) => {
    e.preventDefault();
    const idToken = await user.getIdToken();

    try {
      const response = await axios.post(
        'http://localhost:5001/submit',
        { ...formData, userId: user.uid },
        { headers: { Authorization: `Bearer ${idToken}` } }
      );
      console.log(response.data.message);
      navigate('/thank-you', { state: { email: formData.email, phoneNumber: formData.phoneNumber } });
    } catch (error) {
      console.error("Error submitting form: ", error);
      alert("Failed to submit form!");
    }
  };

  const handleLogout = () => {
    signOut(auth).then(() => {
      setUser(null);
    }).catch((error) => {
      console.error("Error during logout:", error);
    });
  };

  if (!user) {
    return <LoginPage setUser={setUser} />;
  }

  const HomeContent = () => {
    const navigate = useNavigate();

    return (
      <main className="flex-grow bg-gray-100 flex flex-col justify-center items-center py-12">
        {/* Your existing content */}
      </main>
    );
  };

  return (
    <div className="flex flex-col min-h-screen">
      <Header />
      <div className="absolute top-4 right-4">
        <button
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 text-white font-semibold py-2 px-4 rounded-md"
        >
          Logout
        </button>
      </div>
      <Routes>
        <Route path="/" element={<HomeContent />} />
        <Route path="/thank-you" element={<ThankYouPage />} />
      </Routes>
      <Footer />
    </div>
  );
};

export default HomePage;
